sql.where:
AND t.siteId = #{correctiveMaintenance.siteId}

    AND t.type like '%#{correctiveMaintenance.type}%'


    AND t.code like '%${correctiveMaintenance.code}%'


    AND t.state = #{correctiveMaintenance.state}


    AND t.faultCode LIKE '%${correctiveMaintenance.faultCode}%'


    AND t.repairUserId = #{correctiveMaintenance.repairUserId}


    AND t.repairUserName = '%#{correctiveMaintenance.repairUserName}%'


    AND t.happenTime between #{correctiveMaintenance.startTime} and #{correctiveMaintenance.endTime}


    AND mdt.DEVICE_TYPE like '%${correctiveMaintenance.deviceType}%'

AND t.siteId = #{correctiveMaintenance.siteId}

    AND t.type = #{correctiveMaintenance.type}


    AND t.code LIKE '${correctiveMaintenance.code}%'


    AND t.repairNo LIKE '${correctiveMaintenance.repairNo}%'


    AND t.state = #{correctiveMaintenance.state}


    AND t.faultCode LIKE '${correctiveMaintenance.faultCode}%'


    AND t.repairUserId = #{correctiveMaintenance.repairUserId}


    AND t.repairUserName = #{correctiveMaintenance.repairUserName}


    AND t.happenTime between #{correctiveMaintenance.startTime} and #{correctiveMaintenance.endTime}


    AND mdt.DEVICE_TYPE LIKE '${correctiveMaintenance.deviceType}%'

AND t.siteId = #{correctiveMaintenance.siteId}
AND t.planRepairUserId = #{correctiveMaintenance.planRepairUserId}

    AND t.type = #{correctiveMaintenance.type}


    AND t.code like '${correctiveMaintenance.code}%'


    AND t.state = #{correctiveMaintenance.state}


    AND t.repairUserId = #{correctiveMaintenance.repairUserId}


    AND t.repairUserName = #{correctiveMaintenance.repairUserName}


    AND t.faultCode LIKE '${correctiveMaintenance.faultCode}%'


    AND t.happenTime between #{correctiveMaintenance.startTime} and #{correctiveMaintenance.endTime}


    AND md.DEVICE_TYPE like '${correctiveMaintenance.deviceType}%'

mapper.select:
select t.id as id, t.repairNo as repairNo, t.state as state, t.code as code, t.repairUserId as repairUserId, t.repairUserName as repairUserName, t.maintenanceCost as maintenanceCost, t.repairApplicant as repairApplicant, md.DEVICE_NAME as deviceName, t.faultCode as faultCode, mrf.remark as faultDesc, t.repairMethod as repairMethod, t.createTime as createTime, t.productionLine as productionLine, t.station as station, t.happenTime as happenTime, t.repairStartTime as repairStartTime, t.repairEndTime as repairEndTime, t.urgencyLevel as urgencyLevel, t.siteId as siteId, md.COVER_IMG as coverImg from m_repair_corrective_maintenance t left join m_device md on t.code = md.DEVICE and t.siteId = md.SITE left join m_repair_fault mrf on t.faultCode = mrf.faultCode and t.siteId = mrf.siteId
inner join m_repair_corrective_user u on u.REPAIR_ID = t.id and u.REPAIR_USER_ID = #{correctiveMaintenance.rlatedToMe}
inner join m_repair_corrective_user u on u.REPAIR_ID = t.id and u.REPAIR_USER_ID = #{correctiveMaintenance.repairUserId}
order by t.state asc, t.urgencyLevel asc, t.createTime desc

select t.id as id, t.repairNo as repairNo, t.state as state, t.code as code, t.repairUserId as repairUserId, t.repairUserName as repairUserName, t.maintenanceCost as maintenanceCost, t.repairApplicant as repairApplicant, md.DEVICE_NAME as deviceName, t.faultCode as faultCode, mrf.remark as faultDesc, t.repairMethod as repairMethod, t.createTime as createTime, t.urgencyLevel as urgencyLevel, t.productionLine as productionLine, t.station as station, t.happenTime as happenTime, t.repairStartTime as repairStartTime, t.repairEndTime as repairEndTime, t.siteId as siteId from m_repair_corrective_maintenance t left join m_device md on t.code = md.DEVICE and t.siteId = md.SITE left join m_repair_fault mrf on t.faultCode = mrf.faultCode and t.siteId = mrf.siteId
inner join m_repair_corrective_user u on u.REPAIR_ID = t.id and u.REPAIR_USER_ID = #{correctiveMaintenance.rlatedToMe}
inner join m_repair_corrective_user u on u.REPAIR_ID = t.id and u.REPAIR_USER_ID = #{correctiveMaintenance.repairUserId}
order by t.state asc,t.urgencyLevel asc

mapper.update:
update
andon_record
set STATE = #{state},
RECEIVE_MAN = #{user},
RECEIVE_TIME = #{date}
where
SITE = #{site}
and REPAIR_NO = #{repairNo}

mapper.select:
SELECT STATE, COUNT (*) AS count FROM m_repair_corrective_maintenance WHERE AND site = #{site}
AND t.happenTime between #{correctiveMaintenance.startTime} and #{correctiveMaintenance.endTime}
GROUP BY STATE
