mapper.select:
select a.BO as bo, a.PRINT_DATE as printDate, b.${labelPrintQueryDto.queryColumn} as elementCode, a.BAR_CODE_AMOUNT as barCodeAmount, a.START_CODE as startCode, a.END_CODE as endCode, a.TEMPLATE_TYPE as templateType, a.LODOP_TEXT as lodopText, a.IS_COMPLEMENT as isComplement, a.RULE_LABEL_BO as ruleLabelBo, l.LABEL AS label, irl.LABEL_TYPE AS labelType, irl.RULE_LABEL AS ruleLabel, irl.RULE_LABEL_NAME AS ruleLabelName, cr.CODE_RULE_TYPE AS codeRuleType, a.CREATE_DATE AS createDate from label_label_print a left join ${labelPrintQueryDto.tableName} b on a.ELEMENT_BO = b.BO left join label_rule_label irl on a.RULE_LABEL_BO = irl.BO left join m_code_rule cr on cr.BO = irl.CODE_RULE_BO left join label_label l on l.ID = irl.LABEL_BO where a.site = #{labelPrintQueryDto.site} and a.ELEMENT_TYPE = #{labelPrintQueryDto.elementType}
AND CONVERT(nvarchar(10),a.PRINT_DATE,121) = #{labelPrintQueryDto.printDate}
AND CONVERT(nvarchar(10),a.CREATE_DATE,121) = #{labelPrintQueryDto.createDate}
AND b.${labelPrintQueryDto.queryColumn} like CONCAT(#{labelPrintQueryDto.elementCode},'%')
ORDER BY a.CREATE_DATE DESC

SELECT TOP 1 BO
FROM m_code_rule
WHERE CODE_RULE_TYPE = #{code}
  AND SITE = #{site}

SELECT BO
FROM m_code_rule
WHERE CODE_RULE_TYPE = #{code}
  AND SITE = #{site}
  AND ROWNUM  <=  1

select a.BO as bo, a.PRINT_DATE as printDate, pp.PACK_NAME as elementCode, a.BAR_CODE_AMOUNT as barCodeAmount, a.START_CODE as startCode, a.END_CODE as endCode, a.TEMPLATE_TYPE as templateType, a.LODOP_TEXT as lodopText, a.IS_COMPLEMENT as isComplement, a.RULE_LABEL_BO as ruleLabelBo, l.LABEL AS label, irl.LABEL_TYPE AS labelType, irl.RULE_LABEL AS ruleLabel, irl.RULE_LABEL_NAME AS ruleLabelName, cr.CODE_RULE_TYPE AS codeRuleType, pp.PACK_NAME as packingName, a.CREATE_DATE AS createDate from label_label_print a left join p_packing pp on a.ELEMENT_BO = pp.BO left join label_rule_label irl on a.RULE_LABEL_BO = irl.BO left join m_code_rule cr on cr.BO = irl.CODE_RULE_BO left join label_label l on l.ID = irl.LABEL_BO where a.site = #{labelPrintQueryDto.site} and a.ELEMENT_TYPE = #{labelPrintQueryDto.elementType}
AND CONVERT(nvarchar(10),a.PRINT_DATE,121) = #{labelPrintQueryDto.printDate}
AND CONVERT(nvarchar(10),a.CREATE_DATE,121) = #{labelPrintQueryDto.createDate}
AND pp.${labelPrintQueryDto.queryColumn} like CONCAT(#{labelPrintQueryDto.elementCode},'%')
ORDER BY a.CREATE_DATE DESC

select ITEM_NAME as itemName
from m_item
where BO = #{bo}

select SN as sn
from z_sn
where BO = #{bo}
